css:
table {
    margin: 0 auto;
    border: 1px solid #000;
    border-collapse: collapse;
}

th {
    background: #E6E6E6;
    color: black;
    font-size: 12px;
    font-weight: bold;
    text-align: center; /* Center-align the text in table header cells */
    padding: 6px; /* Add padding to table header cells */
}

#main {
    margin-left: 20px;
    margin-right: 20px;
}

@media all {
    div .printedby {
        position: fixed;
        bottom: 0;
        text-align: center;
        font-size: xx-small;
        width: 100%;
    }
}
